import React, { Component } from 'react';
import NewUser from '../newUser/NewUser';
import Stock from '../stock/Stock';
import axios from 'axios';
import { Row, Col, Card, Table, Button, Badge, Modal, ModalBody, CustomInput, ModalHeader, Input, FormGroup, ModalFooter} from 'reactstrap';

import Validators from "../validators/validators";
import {Bar, Doughnut} from 'react-chartjs-2';





class Reporting extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dataDoughnut : {},
            optionDoughnut : {},
            venteTemp : {}
        }



        };



    requestDataFromDate(){

        var debut = document.getElementById("DateDebut");
        var fin = document.getElementById("DateFin");
        console.log(debut.value);
        console.log(fin.value);
        axios.post(process.env.REACT_APP_API_URL+'/ventes/getVenteDF',

            {
                "DateDebut" : debut.value,
                "DateFin" : fin.value
            },

            ).then(res => {
                    const venteTemp = res.data;


                    this.setState(
                        {venteTemp
                    });
                    console.log(this.venteTemp);
                    let A = this.findPercentageProduct(this.venteTemp);
                    console.log(A);







      })


    }

    findPercentageProduct(list){

        let tempArray = [];
        var countProduct = list.lenght;

        for (let j = 0 ; j < countProduct; j++) {
            let produit = list[j].produit;

            if( !this.tupleFind(tempArray, produit)){
                let occurence = 0;
                for (let i = 0 ; i < countProduct; i++) {
                    if (list[j].produit == produit) {
                        occurence +=1;
                        const tuple = {nom : produit.nom, occurence : occurence/countProduct};
                        tempArray.append(tuple);
                    }
                }
            }

        }
        return tempArray;
    }

    tupleFind(array, object){
        var countProduct = array.lenght;
       for (let j = 0 ; j < countProduct; j++) {
            let tuple = array[j];
            if (tuple.nom == object.nom){
                return true
            }
       }
       return false

    }






    // ----------------------- VUE HTML -----------------------------

    render() {
        return (
            <div>
                < Doughnut
                        data={this.state.dataDoughnut}
                        options= {this.state.optionDoughnut}
                        height={500}
                        width={800}
                />
                {/*<select>
                    <option>Mois</option>
                    <option>année</option>
                </select> */}

                <p> De </p>
                <input type={"date"} id={"DateDebut"}></input>
                <p> à </p>
                <input type={"date"} id={"DateFin"}></input>
                <button onClick={this.requestDataFromDate}>validé</button>


            </div>


        )
    }

}

export default Reporting;
