import Validators from "../validators/validators";
import {Bar, Doughnut} from 'react-chartjs-2';





class Reporting extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dataDoughnut : {},
            optionDoughnut : {},
        }

        };


componentDidMount() {

