import React, { Component } from 'react';
import NewUser from '../newUser/NewUser';
import Stock from '../stock/Stock';
import axios from 'axios';


import './Vente.css';
import Row from "reactstrap/src/Row";
import Col from "reactstrap/src/Col";
import Card from "reactstrap/src/Card";
import Table from "reactstrap/src/Table";
import Validators from "../validators/validators";
import Badge from "reactstrap/src/Badge";
import Button from "reactstrap/src/Button";
import modify from "../newUser/img/edit.svg";
import deleteimg from "../newUser/img/rubbish-bin.svg";

class Vente extends Component {

    constructor(props) {
        super(props);
        this.state = {
            ventes :[],
        }

        };


componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL+'/authBDF/getVentes',
        )
            .then(res =>  {

              });


    }

    handleselectedFile = event => {
    this.setState({
      selectedFile: event.target.files[0],
      loaded: 0,
    })
  }

  handleUpload = () => {


    axios.post(process.env.REACT_APP_API_URL+'/ventes/load',{   data :
                    {
                        'file' : this.state.selectedFile,
                    }
                },
                ).then(res => {

      })

  }

  showVentes() {

        let table = []

        if (Validators.isDefined(this.state.ventes.data)) {
            for (let i=0; i < Object.keys(this.state.ventes.data).length; i++) {
                let vente = this.state.ventes.data[i];
                table.push(
                    <tr>
                        <td> {vente.dateVente} </td>
                        <td> {vente.produit} </td>
                        <td> {vente.lieuDeVie} </td>
                        <td> {vente.selledBy} </td>
                        <td> {vente.purchaseBy} </td>
                        <td> {vente.amount} </td>

                    </tr>);

            }
        }
}


    // ----------------------- VUE HTML -----------------------------

    render() {
        return (
            <div>

                <input type="file" name="" id="" onChange={this.handleselectedFile} />
                <button onClick={this.handleUpload}>Upload</button>
                <div> {Math.round(this.state.loaded,2) } %</div>

                <Row>
                    <Col>
                        <Card>
                        <Table className="Table">
                            <thead>
                            <tr>
                                <th>Date de vente</th>
                                <th>Produit</th>
                                <th>Lieu de vie</th>
                                <th>Vendu par </th>
                                <th>acheté par</th>
                                <th>total</th>
                            </tr>
                            </thead>
                            <tbody>

                              {this.showVentes()}

                            </tbody>
                        </Table>
                        </Card>
                    </Col>
                </Row>

            </div>


        )
    }

}

export default Vente;
